import './winCalendar.css';
import React, { useState, useEffect } from 'react';
import { fetchClasses } from './airTable/Airtable.js';

function WinCalendar() {
  return (
    <div className="calendar">
      {Object.keys(groupedClasses).map((date) => (
        <div key={date} className="calendar-day">
          <h2>{date}</h2>
          {groupedClasses[date].map((classItem) => (
            <div key={classItem.id} className="class-item">
              <div className="class-title" onClick={() => toggleClassDetails(classItem.id)}>
                {classItem.fields.ClassTitle} - {classItem.fields.Time}
              </div>
              {activeClass === classItem.id && (
                <div className="class-details">
                  <p>{classItem.fields.ClassDetail}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}

export default WinCalendar;
