import './AirtableCalendar.css';
import React, { useState, useEffect } from 'react';
import { fetchData } from './Airtable.js';
import DOMPurify from 'dompurify'; // Import DOMPurify for sanitization

function AirtableCalendar() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const loadEvents = async () => {
      const fetchedData = await fetchData();
      setEvents(fetchedData.reverse());
    };

    loadEvents();
  }, []);

  // Function to convert Airtable rich text to HTML
  const convertRichTextToHTML = (richText) => {
    return richText.map((block) => {
      if (block.type === 'text') {
        // Add any additional markup handling here if needed
        const marks = block.marks ? block.marks.map(mark => {
          if (mark.type === 'bold') return '<strong>';
          // Add more marks here if you have other formatting options
          return '';
        }).join('') : '';

        return `${marks}${block.text}${marks ? '</strong>' : ''}`; // Close strong if opened
      }
      return '';
    }).join(''); // Join all parts into a single string
  };

  return (
    <div className="airtableCalendar">
      {events.length === 0 ? 
      (<div className="airTableCalendar-none">No events available</div>) 
      : 
      (<div className="airTableCalendar-exists">
        {events.map((event, index) => (
          <div className="airtableCalendar-container" key={index}>
            <div className="topicTitle">
              <h2 className="pillarNum">{event.name} - {event.title}</h2>
            </div>
            <p><b>{event.date}</b> {event.start} - {event.end}</p>
            <div dangerouslySetInnerHTML={{ __html: DOMPurify.sanitize(convertRichTextToHTML(event.richText)) }} /> {/* Render rich text */}

            <ul>
              <li>{event.bullet1}</li>
              <li>{event.bullet2}</li>
              <li>{event.bullet3}</li>
            </ul>
            <a href={event.link}>{event.link}</a>
          </div>
        ))}
      </div>
      )}
    </div>
  );
}

export default AirtableCalendar;
