import './Testimonies.css';
import { TESTIMONIES } from '../../const.js';

function Testimonies() {
    return (
        <div className="Testimonies">
            <div class="header">
                <h1>Our Testimoniess</h1>
            </div>

            <div className="testimoniesGrid">
            {TESTIMONIES.map((testimonies) => {
                return (
                    <div className='testimoniesContainer'>
                            <h3>{testimonies.name}</h3>
                            <div className='languageText'>

                        <div className='testimoniesBox'>
                        <a href={testimonies.link} target='_blank' rel='noopener noreferrer'>
                            <img className='contentImage' src={testimonies.src} alt='' />
                            <p>{testimonies.desc}</p>
                        </a>
                        <a href={testimonies.link}> learn more </a>
                        </div>
                        </div>
                    </div>
                );
            })}
            </div>
        </div>
    );
}

export default Testimonies
