import { useEffect, useState } from 'react';
import './winCalendar.css';
import { Calendar } from '@fullcalendar/core';
import dayGridPlugin from '@fullcalendar/daygrid';
import googleCalendarPlugin from '@fullcalendar/google-calendar';
import listPlugin from '@fullcalendar/list';

const googleCalendarApiKey = 'AIzaSyCNuPc8INoIhejkZ0ZpBgqxMMubNbs7Twc';
const juneCohortId = 'c_da135cf666ae629bc8c39bfbe7a099540d70593c448d73a9fec7cd8c3578e4ba@group.calendar.google.com';
const julyCohortId = 'c_56ec5181422e91d15aeb51914a84ebfdfc8129ed04652867d24f67feadf3bb87@group.calendar.google.com';

function WinCalendar() {
  const [selectedCohort, setSelectedCohort] = useState(juneCohortId); // Default to June Cohort

  useEffect(() => {
    const calendarEl = document.getElementById('calendar');

    const calendar = new Calendar(calendarEl, {
      plugins: [dayGridPlugin, googleCalendarPlugin, listPlugin],
      initialView: 'listYear',

      headerToolbar: {
        left: 'prev next today',
        center: 'title',
        right: 'listYear dayGridMonth',
      },

      titleFormat: () => {
        return 'June 12, 2024 – October 22, 2024';
      },

      validRange: {
        start: '2024-06-01',
        end: '2024-11-01',
      },

      googleCalendarApiKey: googleCalendarApiKey,

      eventSources: [
        {
          googleCalendarId: selectedCohort, // Use the selected cohort
          className: selectedCohort === juneCohortId ? 'June Cohort' : 'July Cohort',
        },
      ],
    });

    calendar.render(); // Render the calendar when the component mounts

    return () => {
      calendar.destroy(); // Clean up the calendar when the component unmounts
    };
  }, [selectedCohort]); // Re-run the effect when the selected cohort changes

  return (
    <div className="winCalendar">
      {/* Dropdown for selecting the cohort */}
      <select
        value={selectedCohort}
        onChange={(e) => setSelectedCohort(e.target.value)} // Update state when selecting a different cohort
      >
        <option value={juneCohortId}>June Cohort</option>
        <option value={julyCohortId}>July Cohort</option>
      </select>

      <div id="calendar"></div>
    </div>
  );
}

export default WinCalendar;
