import './ScrollComponent.css';

function ScrollComponent() {
  const scrollToElement = () => {
    const element = document.getElementById("targetElement");
    if (element) {
      element.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <div>
      {/* Button to scroll to the element */}
      <button onClick={scrollToElement}>Go to Section</button>

      {/* Other content */}
      <div style={{ height: '150vh' }}> {/* Simulating some content before the target */}
        Scroll down to the target element
      </div>

      {/* Target Element */}
      <div id="targetElement" style={{ height: '200px', background: 'lightgray' }}>
        This is the target element!
      </div>
    </div>
  );
}

export default ScrollComponent;
