// Airtable.js
import axios from 'axios';

const AIRTABLE_API_KEY = 'your_airtable_api_key';  // Replace with your Airtable API key
const BASE_ID = 'your_base_id';  // Replace with your Airtable Base ID
const TABLE_NAME = 'Classes';  // Replace with your Airtable Table Name

const airtableAPI = axios.create({
  baseURL: `https://api.airtable.com/v0/${BASE_ID}/${TABLE_NAME}`,
  headers: {
    Authorization: `Bearer ${AIRTABLE_API_KEY}`,
  },
});

// Fetch classes from Airtable
export const fetchClasses = async () => {
  try {
    const response = await airtableAPI.get();
    return response.data.records;
  } catch (error) {
    console.error('Error fetching classes from Airtable:', error);
    return [];
  }
};
