import './Calendar.css';
import { useEffect, useRef } from 'react'; // Import React hooks
import { Calendar as FullCalendar } from '@fullcalendar/core'; // Renamed FullCalendar to avoid conflict
import googleCalendarPlugin from '@fullcalendar/google-calendar';

function Calendar() {
  const calendarRef = useRef(null); // Reference to the calendar div

  useEffect(() => {
    // Initialize the FullCalendar after the component mounts
    let calendar = new FullCalendar(calendarRef.current, {
      plugins: [googleCalendarPlugin],
      googleCalendarApiKey: 'AIzaSyCNuPc8INoIhejkZ0ZpBgqxMMubNbs7Twc',
      events: {
        googleCalendarId: 'c_6ac1b95b43f0bc6b7729121b7f5eb6af644b44192b24c7cd2a648282e9d5df0d@group.calendar.google.com'
      }
    });

    // Render the calendar
    calendar.render();

    // Cleanup when the component unmounts
    return () => {
      calendar.destroy(); // Clean up the calendar instance
    };
  }, []); // Empty dependency array means this runs once after the component mounts

  return (
    <div id="calendar" ref={calendarRef}> {/* Attach ref to this div */}
      <div className="header">
        <h1>CALENDAR SECTION</h1>
      </div>
    </div>
  );
}

export default Calendar;
